<? if (!defined("B_PROLOG_INCLUDED") || B_PROLOG_INCLUDED !== true) die();/** * @author Lukmanov Mikhail <lukmanof92@gmail.com> */if ($arResult) { ?>    <ul class="header-top_navbar">        <?        $previousLevel = 0;        foreach ($arResult            as $arItem){        if ($previousLevel && $arItem["DEPTH_LEVEL"] < $previousLevel) {            echo str_repeat("</ul></li>", ($previousLevel - $arItem["DEPTH_LEVEL"]));        }        if ($arItem["IS_PARENT"]){        if ($arItem["DEPTH_LEVEL"] == 1){        ?>        <li class="<?= $arItem['SELECTED'] ? 'active' : '' ?>">            <a href="<?= $arItem["LINK"] ?>"><?= $arItem["TEXT"] ?></a>            <ul class="header-top_submenu">                <? } else { ?>                <li class="<?= $arItem['SELECTED'] ? 'nav-sidebar-list-item_active' : '' ?>">                    <a href="<?= $arItem["LINK"] ?>"><?= $arItem["TEXT"] ?></a>                    <ul class="header-top_submenu">                        <?                        }                        } else {                            if ($arItem["PERMISSION"] > "D") {                                if ($arItem["DEPTH_LEVEL"] == 1) {                                    ?>                                    <li>                                        <a href="<?= $arItem["LINK"] ?>"><?= $arItem["TEXT"] ?></a>                                    </li>                                <? } else { ?>                                    <li class="<?= $arItem['SELECTED'] ? 'active' : '' ?>">                                        <a href="<?= $arItem["LINK"] ?>"><?= $arItem["TEXT"] ?></a>                                    </li>                                <?                                }                            } else {                                if ($arItem["DEPTH_LEVEL"] == 1) {                                    ?>                                    <li>                                        <a href=""                                           title="<?= GetMessage("MENU_ITEM_ACCESS_DENIED") ?>"><?= $arItem["TEXT"] ?></a>                                    </li>                                <? } else { ?>                                    <li>                                        <a href="" class="denied"                                           title="<?= GetMessage("MENU_ITEM_ACCESS_DENIED") ?>"><?= $arItem["TEXT"] ?></a>                                    </li>                                <?                                }                            }                        }                        $previousLevel = $arItem["DEPTH_LEVEL"];                        }                        if ($previousLevel > 1) {                            echo str_repeat("</ul></li>", ($previousLevel - 1));                        } ?>    </ul>                    <? } ?>