<? if (!defined("B_PROLOG_INCLUDED") || B_PROLOG_INCLUDED !== true) die();/** @var array $arParams *//** @var array $arResult *//** @global CMain $APPLICATION *//** @global CUser $USER *//** @global CDatabase $DB *//** @var CBitrixComponentTemplate $this *//** @var string $templateName *//** @var string $templateFile *//** @var string $templateFolder *//** @var string $componentPath *//** @var CBitrixComponent $component */$this->setFrameMode(true);use Bitrix\Main\Localization\Loc;Loc::loadMessages(__FILE__);if ($arResult['ITEMS']) { ?>    <div class="last__news-wrapper">        <div class="sidebar__title">            <h4><?= Loc::getMessage('LAST_STOCK_TITLE') ?></h4>        </div>        <? foreach ($arResult['ITEMS'] as $arNews) {            $this->AddEditAction($arNews['ID'], $arNews['EDIT_LINK'], CIBlock::GetArrayByID($arNews["IBLOCK_ID"], "NEWS_EDIT"));            $this->AddDeleteAction($arNews['ID'], $arNews['DELETE_LINK'], CIBlock::GetArrayByID($arNews["IBLOCK_ID"], "NEWS_DELETE"), array("CONFIRM" => GetMessage('CT_BCSL_ELEMENT_DELETE_CONFIRM')));            ?>            <div class="last__news-item" id="<?= $this->GetEditAreaId($arNews['ID']); ?>">                <span class="last__news-date"><?= $arNews['DISPLAY_ACTIVE_FROM'] ? $arNews['DISPLAY_ACTIVE_FROM'] : $arNews['ACTIVE_FROM'] ?></span>                <a href="<?= $arNews['DETAIL_PAGE_URL'] ?>" class="last__news-name"><?= $arNews['NAME'] ?></a>            </div>        <? } ?>        <a href="<?= SITE_DIR ?>stocks/" class="btn btn-link"><?= Loc::getMessage('ALL_STOCKS') ?></a>    </div><? } ?>